        }


        [Fact]
        public void EqualityOperators()
        {
            MolarEntropy a = MolarEntropy.FromJoulesPerMoleKelvin(1);
            MolarEntropy b = MolarEntropy.FromJoulesPerMoleKelvin(2);

// ReSharper disable EqualExpressionComparison
            Assert.True(a == a);
            Assert.True(a != b);

            Assert.False(a == b);
