using System;
using UnitsNet.Units;
using Xunit;

// Disable build warning CS1718: Comparison made to same variable; did you mean to compare something else?
#pragma warning disable 1718

// ReSharper disable once CheckNamespace
namespace UnitsNet.Tests
{
    /// <summary>
    /// Test of ReactivePowerGradient.
    /// </summary>
// ReSharper disable once PartialTypeWithSinglePart
    public abstract partial class ReactivePowerGradientTestsBase
    {
        protected abstract double KilovoltamperesPerSecondInOneVoltamperePerSecond { get; }
        protected abstract double VoltamperesPerSecondInOneVoltamperePerSecond { get; }

// ReSharper disable VirtualMemberNeverOverriden.Global
        protected virtual double KilovoltamperesPerSecondTolerance { get { return 1e-5; } }
        protected virtual double VoltamperesPerSecondTolerance { get { return 1e-5; } }
// ReSharper restore VirtualMemberNeverOverriden.Global

        [Fact]
        public void VoltamperePerSecondToReactivePowerGradientUnits()
        {
            ReactivePowerGradient voltamperepersecond = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            AssertEx.EqualTolerance(KilovoltamperesPerSecondInOneVoltamperePerSecond, voltamperepersecond.KilovoltamperesPerSecond, KilovoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(VoltamperesPerSecondInOneVoltamperePerSecond, voltamperepersecond.VoltamperesPerSecond, VoltamperesPerSecondTolerance);
        }

        [Fact]
        public void FromValueAndUnit()
        {
            AssertEx.EqualTolerance(1, ReactivePowerGradient.From(1, ReactivePowerGradientUnit.KilovoltamperePerSecond).KilovoltamperesPerSecond, KilovoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(1, ReactivePowerGradient.From(1, ReactivePowerGradientUnit.VoltamperePerSecond).VoltamperesPerSecond, VoltamperesPerSecondTolerance);
        }

        [Fact]
        public void As()
        {
            var voltamperepersecond = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            AssertEx.EqualTolerance(KilovoltamperesPerSecondInOneVoltamperePerSecond, voltamperepersecond.As(ReactivePowerGradientUnit.KilovoltamperePerSecond), KilovoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(VoltamperesPerSecondInOneVoltamperePerSecond, voltamperepersecond.As(ReactivePowerGradientUnit.VoltamperePerSecond), VoltamperesPerSecondTolerance);
        }

        [Fact]
        public void ConversionRoundTrip()
        {
            ReactivePowerGradient voltamperepersecond = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            AssertEx.EqualTolerance(1, ReactivePowerGradient.FromKilovoltamperesPerSecond(voltamperepersecond.KilovoltamperesPerSecond).VoltamperesPerSecond, KilovoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(1, ReactivePowerGradient.FromVoltamperesPerSecond(voltamperepersecond.VoltamperesPerSecond).VoltamperesPerSecond, VoltamperesPerSecondTolerance);
        }

        [Fact]
        public void ArithmeticOperators()
        {
            ReactivePowerGradient v = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            AssertEx.EqualTolerance(-1, -v.VoltamperesPerSecond, VoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(2, (ReactivePowerGradient.FromVoltamperesPerSecond(3)-v).VoltamperesPerSecond, VoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(2, (v + v).VoltamperesPerSecond, VoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(10, (v*10).VoltamperesPerSecond, VoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(10, (10*v).VoltamperesPerSecond, VoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(2, (ReactivePowerGradient.FromVoltamperesPerSecond(10)/5).VoltamperesPerSecond, VoltamperesPerSecondTolerance);
            AssertEx.EqualTolerance(2, ReactivePowerGradient.FromVoltamperesPerSecond(10)/ReactivePowerGradient.FromVoltamperesPerSecond(5), VoltamperesPerSecondTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            ReactivePowerGradient oneVoltamperePerSecond = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            ReactivePowerGradient twoVoltamperesPerSecond = ReactivePowerGradient.FromVoltamperesPerSecond(2);

            Assert.True(oneVoltamperePerSecond < twoVoltamperesPerSecond);
            Assert.True(oneVoltamperePerSecond <= twoVoltamperesPerSecond);
            Assert.True(twoVoltamperesPerSecond > oneVoltamperePerSecond);
            Assert.True(twoVoltamperesPerSecond >= oneVoltamperePerSecond);

            Assert.False(oneVoltamperePerSecond > twoVoltamperesPerSecond);
            Assert.False(oneVoltamperePerSecond >= twoVoltamperesPerSecond);
            Assert.False(twoVoltamperesPerSecond < oneVoltamperePerSecond);
            Assert.False(twoVoltamperesPerSecond <= oneVoltamperePerSecond);
        }

        [Fact]
        public void CompareToIsImplemented()
        {
            ReactivePowerGradient voltamperepersecond = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            Assert.Equal(0, voltamperepersecond.CompareTo(voltamperepersecond));
            Assert.True(voltamperepersecond.CompareTo(ReactivePowerGradient.Zero) > 0);
            Assert.True(ReactivePowerGradient.Zero.CompareTo(voltamperepersecond) < 0);
        }

        [Fact]
        public void CompareToThrowsOnTypeMismatch()
        {
            ReactivePowerGradient voltamperepersecond = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            Assert.Throws<ArgumentException>(() => voltamperepersecond.CompareTo(new object()));
        }

        [Fact]
        public void CompareToThrowsOnNull()
        {
            ReactivePowerGradient voltamperepersecond = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            Assert.Throws<ArgumentNullException>(() => voltamperepersecond.CompareTo(null));
        }


        [Fact]
        public void EqualityOperators()
        {
            ReactivePowerGradient a = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            ReactivePowerGradient b = ReactivePowerGradient.FromVoltamperesPerSecond(2);

// ReSharper disable EqualExpressionComparison
            Assert.True(a == a);
            Assert.True(a != b);

            Assert.False(a == b);
            Assert.False(a != a);
// ReSharper restore EqualExpressionComparison
        }

        [Fact]
        public void EqualsIsImplemented()
        {
            ReactivePowerGradient v = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            Assert.True(v.Equals(ReactivePowerGradient.FromVoltamperesPerSecond(1), ReactivePowerGradient.FromVoltamperesPerSecond(VoltamperesPerSecondTolerance)));
            Assert.False(v.Equals(ReactivePowerGradient.Zero, ReactivePowerGradient.FromVoltamperesPerSecond(VoltamperesPerSecondTolerance)));
        }

        [Fact]
        public void EqualsReturnsFalseOnTypeMismatch()
        {
            ReactivePowerGradient voltamperepersecond = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            Assert.False(voltamperepersecond.Equals(new object()));
        }

        [Fact]
        public void EqualsReturnsFalseOnNull()
        {
            ReactivePowerGradient voltamperepersecond = ReactivePowerGradient.FromVoltamperesPerSecond(1);
            Assert.False(voltamperepersecond.Equals(null));
        }
    }
}
