            AbsorbedDoseOfIonizingRadiation twoGrays = AbsorbedDoseOfIonizingRadiation.FromGrays(2);

            Assert.True(oneGray < twoGrays);
            Assert.True(oneGray <= twoGrays);
            Assert.True(twoGrays > oneGray);
