            Assert.False(oneVoltPerSecond > twoVoltsPerSecond);
            Assert.False(oneVoltPerSecond >= twoVoltsPerSecond);
            Assert.False(twoVoltsPerSecond < oneVoltPerSecond);
            Assert.False(twoVoltsPerSecond <= oneVoltPerSecond);
        }

