        public void GetHashCode_Equals()
        {
            var quantity = Length.FromMeters(1.0);
            var expected = Comparison.GetHashCode(typeof(Length), quantity.As(Length.BaseUnit));
            Assert.Equal(expected, quantity.GetHashCode());
        }

