                AssertEx.EqualTolerance(1, parsed.KilonewtonsPerCubicMeter, KilonewtonsPerCubicMeterTolerance);
                Assert.Equal(SpecificWeightUnit.KilonewtonPerCubicMeter, parsed.Unit);
