            var expectedUnit = StandardVolumeFlow.Info.GetDefaultUnit(UnitSystem.SI);
            var expectedValue = quantity.As(expectedUnit);

            Assert.Multiple(() =>
            {
                StandardVolumeFlow quantityToConvert = quantity;

