            var fromUnit = TemperatureDelta.Units.First(u => u != TemperatureDelta.BaseUnit);

            var quantity = TemperatureDelta.From(3.0, fromUnit);
            var converted = quantity.ToUnit(unit);
            Assert.Equal(converted.Unit, unit);
