        [Theory]
        [MemberData(nameof(UnitTypes))]
        public void ToUnit_FromIQuantity_ReturnsTheExpectedIQuantity(TemperatureUnit unit)
        {
            var quantity = Temperature.From(3, Temperature.BaseUnit);
