            if(!(unit is BitRateUnit unitAsBitRateUnit))
                throw new ArgumentException($"The given unit is of type {unit.GetType()}. Only {typeof(BitRateUnit)} is supported.", nameof(unit));

            return As(unitAsBitRateUnit);
        }

