        /// <summary>Get <see cref="AmountOfSubstance"/> from <see cref="Energy"/> / <see cref="MolarEnergy"/>.</summary>
        public static AmountOfSubstance operator /(Energy energy, MolarEnergy molarEnergy)
        {
