        {
            if (!(unit is VolumeUnit unitAsVolumeUnit))
                throw new ArgumentException($"The given unit is of type {unit.GetType()}. Only {typeof(VolumeUnit)} is supported.", nameof(unit));

            return As(unitAsVolumeUnit);
