                return new ElectricAdmittance(double.MaxValue);
            }
        }

        /// <summary>
        /// Represents the smallest possible value of ElectricAdmittance
        /// </summary>
        public static ElectricAdmittance MinValue
        {
            get
            {
                return new ElectricAdmittance(double.MinValue);
            }
        }
    }
}
